<?phpinclude 'mysql_config.php';// REASON $reason="wrong_password" or "other"// $username is above this filefunction account_block($username,$reason){	if($reason=="wrong_password"){	$query_block="UPDATE `tbl_user` SET `status`='blocked_wp' WHERE `username`='$username'";	mysql_query($query_block);		if( !mysql_error() ){		return true;	}else{		return false;	}	}}?>